function App() {
  const [aluno,setAluno] = useState('Aluno SI')

function changeName(){
  setAluno('Keren')
  return(
    <div>
      <h1>Aula 03</h1>
      <h2>Ol√° {aluno}</h2>
      <button onClick={changeName}>
        Mudar nome
      </button>
      <Name aluno = "Keren" idade = "20"/>   
      
      <form>
        <label>Nome: </label> 
        <input placeholder='Digite seu nome' value={aluno}
        />
    
      </form>
    </div>
  );
  }
}
export default App;
