import {useState} from 'react';

function App() {
  const [aluno,setAluno] = useState('')
  const [email,setEmail] = useState('')
  const [idade,setIdade] = useState('')



function changeName(){
  setAluno('Keren')
}
  return(
    <div>
      <h1>Aula 03</h1>
      <h2>Ol√° {aluno}</h2>
      <button onClick={changeName}>
        Mudar nome
      </button>
      <Nome aluno = "Keren" idade = "20" email="kerenbernal@unasp.com"/>   
      
      <form>
        <label>Nome: </label> 
        <input placeholder='Digite seu nome' value={aluno} onChange={(e) => setAluno(e.target.value)} />
        <label>Email: </label> 
        <input placeholder='Digite seu email' value={idade} onChange={(e) => setEmail(e.target.value)} />
        <label>Idade: </label> 
        <input paceholder='Digite sua idade' value={email} onChange={(e) => setIdade(e.target.value)} />
      </form>
    </div>
  );
  }
  function Nome(props){
    return(
      <p>(props.aluno)- (props.aluno)</p>
    );
  }
export default App;
